mapper.select:
SELECT COM_SEQ, USER_ID, BBS_SEQ, COM_CONTENT, COM_GROUP, COM_PARENT, COM_ORDER, COM_INDENT, COM_STATUS, COM_REG_DATE, USER_NAME FROM ( SELECT ROWNUM RNUM, COM_SEQ, USER_ID, BBS_SEQ, COM_CONTENT, COM_GROUP, COM_PARENT, COM_ORDER, COM_INDENT, COM_STATUS, COM_REG_DATE, USER_NAME FROM ( SELECT C.COM_SEQ COM_SEQ, U.USER_ID USER_ID, C.BBS_SEQ BBS_SEQ, NVL(C.COM_CONTENT, '') COM_CONTENT, NVL(C.COM_GROUP, 0) COM_GROUP, NVL(C.COM_PARENT, 0) COM_PARENT, NVL(C.COM_ORDER, 0) COM_ORDER, NVL(C.COM_INDENT, 0) COM_INDENT, NVL(C.COM_STATUS, 'N') COM_STATUS, NVL(TO_CHAR(C.COM_REG_DATE, 'YYYY-MM-DD HH24:MI:SS'), '') COM_REG_DATE, NVL(U.USER_NAME, '') USER_NAME, NVL(R.GROUP_REPLY_CNT, 0) GROUP_REPLY_CNT FROM COM C, USER_TABLE U, (SELECT COM_GROUP, COUNT(COM_SEQ) GROUP_REPLY_CNT FROM ( SELECT COM_GROUP, COM_SEQ, COM_PARENT FROM COM WHERE COM_PARENT = 0 START WITH COM_PARENT = 0 CONNECT BY PRIOR COM_SEQ = COM_PARENT AND LEVEL > 1 ) GROUP BY COM_GROUP) R WHERE 1 = 1
AND C.USER_ID = #{myPageUserId} AND C.COM_STATUS = 'Y'
AND C.BBS_SEQ = #{bbsSeq}
AND C.USER_ID = U.USER_ID AND C.COM_GROUP = R.COM_GROUP
ORDER BY C.COM_SEQ DESC
ORDER BY C.COM_GROUP, C.COM_ORDER ORDER BY C.COM_GROUP DESC, C.COM_ORDER ORDER BY GROUP_REPLY_CNT DESC, COM_GROUP, COM_ORDER
) ) WHERE RNUM BETWEEN #{startRow} AND #{endRow}

SELECT COUNT(COM_SEQ) FROM COM WHERE 1 = 1
AND USER_ID = #{myPageUserId} AND COM_STATUS = 'Y'
AND BBS_SEQ = #{bbsSeq}
