mapper.select:
SELECT HIBBS_SEQ, USER_ID, USER_NAME, USER_EMAIL, HIBBS_GROUP, HIBBS_ORDER, HIBBS_INDENT, HIBBS_TITLE, HIBBS_CONTENT, HIBBS_READ_CNT, REG_DATE, HIBBS_PARENT FROM ( SELECT ROWNUM RNUM, HIBBS_SEQ, USER_ID, USER_NAME, USER_EMAIL, HIBBS_GROUP, HIBBS_ORDER, HIBBS_INDENT, HIBBS_TITLE, HIBBS_CONTENT, HIBBS_READ_CNT, REG_DATE, HIBBS_PARENT FROM ( SELECT A.HIBBS_SEQ HIBBS_SEQ, A.USER_ID USER_ID, NVL(B.USER_NAME, '') USER_NAME, NVL(B.USER_EMAIL, '') USER_EMAIL, NVL(A.HIBBS_GROUP, 0) HIBBS_GROUP, NVL(A.HIBBS_ORDER, 0) HIBBS_ORDER, NVL(A.HIBBS_INDENT, 0) HIBBS_INDENT, NVL(A.HIBBS_TITLE, '') HIBBS_TITLE, NVL(A.HIBBS_CONTENT, '') HIBBS_CONTENT, NVL(A.HIBBS_READ_CNT, 0) HIBBS_READ_CNT, NVL(TO_CHAR(A.REG_DATE, 'YYYY-MM-DD HH24:MI:SS'), '') REG_DATE, NVL(A.HIBBS_PARENT, '') HIBBS_PARENT FROM TBL_HIBOARD A, TBL_USER B WHERE A.USER_ID = B.USER_ID
AND B.USER_NAME LIKE '%'|| #{searchValue} ||'%' AND A.HIBBS_TITLE LIKE '%'|| #{searchValue} ||'%' AND DBMS_LOB.INSTR(A.HIBBS_CONTENT, #{searchValue}) > 0
ORDER BY A.HIBBS_GROUP DESC, A.HIBBS_ORDER ASC ) ) WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
        UPDATE TBL_HIBOARD
		SET HIBBS_READ_CNT = HIBBS_READ_CNT + 1
		WHERE HIBBS_SEQ = #{value}

UPDATE TBL_HIBOARD
SET HIBBS_ORDER = HIBBS_ORDER + 1
WHERE HIBBS_GROUP = #{hiBbsGroup} 
    AND HIBBS_ORDER >= #{hiBbsOrder}

UPDATE TBL_HIBOARD 
SET HIBBS_TITLE = #{hiBbsTitle}, HIBBS_CONTENT = #{hiBbsContent} 
WHERE HIBBS_SEQ = #{hiBbsSeq}
